       direct_memories_source={
           "direct_memories": [{"fact": query}]
       },
       scope={
           "user_id": tool_context._invocation_context.user_id,
           "app_name": tool_context._invocation_context.app_name
       },
       config={"wait_for_completion": False}
   )
   return {"status": "success"}
